mapper.select:

SELECT * FROM alto_File
WHERE notice_num = #{notice_num}
ORDER BY FILENO

mapper.update:

UPDATE alto_club_notice 
	SET TITLE = #{title}
		,contents = #{contents}
	WHERE notice_num = #{notice_num}

choose.when:
UPDATE alto_File 
	SET FILENAME = #{item.fileName}
WHERE 
	notice_num = #{item.notice_num} 
	AND FILENO = #{item.fileNO}

mapper.delete:
DELETE FROM alto_File
WHERE notice_num = #{notice_num}
AND FILENO = #{FileNO}

mapper.update:

UPDATE alto_club_notice
SET score = score + 1
WHERE notice_num = #{notice_num}
