mapper.select:
SELECT ca.*
FROM ALTO_CART ca
WHERE ca.GOODS_CODE = #{goods_code}
AND MEM_ID = #{mem_id}
AND GOODS_TYPE = #{goods_type}

mapper.update:
UPDATE ALTO_CART
SET QUANTITY = QUANTITY + #{quantity}
WHERE GOODS_CODE = #{goods_code}
AND MEM_ID = #{mem_id}
AND GOODS_TYPE = #{goods_type}

mapper.delete:
DELETE FROM EZEN.ALTO_CART
WHERE GOODS_CODE = #{goods_code}
AND MEM_ID = #{mem_id}
AND GOODS_TYPE = #{goods_type}
